<template>
  <ASkeleton v-if="loadingState.isLoading.value" active />

  <template v-else-if="counterparty">
    <ARow :gutter="20">
      <ACol flex="200px">
        <!-- TODO fallback image -->
        <AImage
          fallback="data:image/png;base64,"
          :src="counterparty.logo"
          :width="200"
        />
      </ACol>
      <ACol flex="auto">
        <ATypographyTitle :level="4">
          {{ counterparty.title }}
        </ATypographyTitle>

        <ATypographyParagraph>
          <ATypographyText type="secondary">
            {{ $t('common.alias') }}:
          </ATypographyText>
          {{ counterparty.alias }}
        </ATypographyParagraph>

        <ATypographyParagraph>
          <ATypographyText type="secondary">
            {{ $t('common.abbr.iinBin') }}:
          </ATypographyText>
          {{ counterparty.bin }}
        </ATypographyParagraph>

        <ATypographyParagraph>
          <ATypographyText type="secondary">
            {{ $t('common.address') }}:
          </ATypographyText>
          {{ counterparty.address }}
        </ATypographyParagraph>

        <ATypographyParagraph>
          <ATypographyText type="secondary">
            {{ $t('common.city') }}:
          </ATypographyText>
          {{ counterparty.city.title }}, {{ counterparty.city.region.title }}
        </ATypographyParagraph>

        <ATypographyParagraph>
          <ATypographyText type="secondary">
            Email:
          </ATypographyText>
          {{ counterparty.email }}
        </ATypographyParagraph>

        <ATypographyParagraph v-if="counterparty.phone">
          <ATypographyText type="secondary">
            {{ $t('common.phone') }}:
          </ATypographyText>
          {{ counterparty.phone }}
        </ATypographyParagraph>
      </ACol>
    </ARow>
  </template>
</template>

<script setup lang="ts">
import type { Nullable, UrlParam } from '@qlt2020/frutils';
import type { CounterpartyInfo } from '../counterparty-info.model';
import { Executor } from '@/shared/network/executor';
import { CounterpartyInfoRepository } from '@/modules/counterparty/counterparty-info/counterparty-info.repository';
import { useLoading } from '@/shared/composables/useLoading';

const props = defineProps<{
  dataId?: string
}>();

const counterparty = ref<Nullable<CounterpartyInfo>>(null);
const loadingState = useLoading();

onMounted(() => {
  readCounterparty(props.dataId as UrlParam);
});

function readCounterparty(dataId: UrlParam) {
  return Executor.run({
    request: CounterpartyInfoRepository.read(dataId),
    loadingState,
    onResult(data) {
      counterparty.value = data;
    },
  });
}
</script>
